mapper.select:
SELECT COUNT(*)
FROM music_like
WHERE member_sq = #{member_sq}
AND target_sq = #{target_sq}

mapper.update:
UPDATE music_like
SET music_like_type = 'LI3' 
WHERE music_like_sq = #{unLike}

UPDATE music_like
SET music_like_type = 'LI4' 
WHERE music_like_sq = #{unLike}

mapper.select:
select count(follow_sq) as cnt
from follow
where member_sq = #{me}
and target_sq = #{target_sq}

mapper.delete:
DELETE FROM follow
WHERE member_sq = #{member_sq}
AND target_sq = #{target_sq}

mapper.select:
SELECT MAX(album_sq)
FROM album
WHERE member_sq = #{member_sq}

SELECT music_sq
FROM music
WHERE album_sq = #{album_sq}

mapper.update:
UPDATE album
SET
	music_sq = #{music_sq}
WHERE
	album_sq = #{album_sq}


	UPDATE reply
	SET
		reply_order = reply_order+1
	WHERE reply_sq != #{reply_sq}
	  AND music_sq = #{music_sq}
	  AND reply_order >= #{reply_order}

mapper.select:
SELECT nvl2(MAX(reply_order),MAX(reply_order),0) reply_order
FROM reply
WHERE music_sq = #{music_sq}
  AND time_stamp BETWEEN 0 AND #{time_stamp}

SELECT count(*)
FROM reply
WHERE music_sq = #{music_sq}
